mapper.update:
update board
set title= #{title},
author=#{author},
content=#{content}
where num = #{num}

mapper.select:
select num, title, author, content, to_char(writeday, 'yyyy/MM/dd')
writeday, readCnt,origin,groupnum,grouplayer
from board where num= #{num}

mapper.update:
update board
set readCnt=(readCnt+1)
where num = #{num}
