mapper.select:
	  select u_id ,u_pw ,u_name, u_email, u_phone, admin 
      from member
      where u_id = #{u_id}

mapper.update:
update member set  
     U_PW = #{u_pw}, 
     U_NAME = #{u_name}, 
     U_EMAIL = #{u_email},
     U_PHONE = #{u_phone}
    where U_ID = #{u_id}

